import React, { Component } from "react";

class Movie extends Component {
  state = {};

  onSeenMovie = (item) => {
    fetch('http://127.0.0.1:5070/movie', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        contentId: item.id,
        title: item.title,
        description: item.overview,
        email: "mathew.anishk@gmail.com",
        contentType: "Movie"
      })
    })
  }

  onAddToList = (item) => {
    fetch('http://127.0.0.1:5070/watch', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        contentId: item.id,
        title: item.title,
        description: item.overview,
        email: "mathew.anishk@gmail.com",
        contentType: "Movie"
      })
    })
  }

  render() {
    const { id, title, overview, poster_path, release_date } = this.props.item;
    console.log(this.props.item);
    return (
      <div class="col-2">
        <div className="card" key={id}>
        <button className="btn btn-danger" onClick={() => this.onAddToList(this.props.item)}>
        Add to Watchlist
        </button>
        <button className="btn btn-primary" onClick={() => this.onSeenMovie(this.props.item)}>
        Watched
        </button>
          <img
            className="card-img-top img-fluid"
            src={`https://image.tmdb.org/t/p/original${poster_path}`}
            alt="Card image cap"
          />
          <div className="card-body">
        

          </div>
        </div>
      </div>
    );
  }
}

export default Movie;
